import React,{useState} from 'react';
//import { PageHeader ,Input,Button} from 'antd';
import {auth} from './firebase'
import { navigate } from "@reach/router"
import "./components/videos/complete.css"
const SignIn = (props) => {
    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')
    const onEmailChange = (event) => setEmail(event.target.value)
    const onPasswordChange=(event)=>setPassword(event.target.value)
    const onSignIn=()=>{
        console.log(email,password)
        auth.signInWithEmailAndPassword(email, password)
        .then(function(result){
            console.log('user signed in')
        }).catch(function(error) {
            // Handle Errors here.
            var errorCode = error.code;
            var errorMessage = error.message;
            // ...
          });
          setEmail('')
          setPassword('')
          navigate(`/`)
    }
    return(
        <div className = "Sign_up_container">
             <div className ="page_header_container">
            {/* <PageHeader
                style={{
                    border: '1px solid rgb(235, 237, 240)',
                }}
                title="Sign In"
            /> */}
            <h2>Sign In</h2>
            </div>
            <div className="sign_up_container_inputs" style ={{marginTop : "20px" }}>
            <div className="post_input_container">
                <div className="post_input_title">
                    <h2>Email</h2>
                </div>
                <div className = "post_input">
                    <input placeholder="Email" onChange={onEmailChange}/>
                </div>
            </div>
            <div className="post_input_container">
                <div className="post_input_title">
                    <h2>Password</h2>
                </div>
                <div className = "post_input">
                    <input placeholder="Password" onChange={onPasswordChange}/>
                </div>
            </div>
            <div style ={{width:'100%'}}>
                <div style={{float:'left'}}>
                    <a href="sign_up">Don't have an account,Sign Up</a>
                </div>
                <div className="post_input_button">
                <button className="btn btn-" type="primary" size="large" onClick={onSignIn}>
                    SignIn
                </button>
                </div>
            </div>

        </div>
        </div>
    )
}
export default SignIn;
