import React, {useState,useEffect} from 'react';

import { connect } from 'react-redux';
import { GET_VOLUNTEERS } from '../../actions/types';

import SearchBar from './search_bar';
import YTSearch from 'youtube-api-search';
import VideoList from './video_list';
import VideoDetail from './video_details';
//import "./complete.css"
import db from '../../firebase'

const API_KEY =" ";//"AIzaSyALY8xL3Y85VTF27ABKdd4HRTihe13beAA";



const CuratedContents =(props)=>{

    
      const state = {
        index: 0,
        indexm:0,
        videos: [1,2,3,4,5],
        motivates: ["Talk to friends or family face-to-face",
          "Spend some time in nature",
          "Read a good book",
          "List what you like about yourself",
          "Do something spontaneous",
          "Listen to music",
          "Take a long, hot bath",
          "Watch a funny movie or TV show"],
        selectVideo: null,
        completed: false,
        
      }
      
    
    useEffect(() => {
      props.getVolunteers();
      console.log(props,"this is video section")
      
      videoSearch(props.mental);

    
    }, [])
      
  const getVideo = (SearchVideo) =>{
    console.log("searchVideo",SearchVideo)
    YTSearch({key: API_KEY, term: SearchVideo,maxValue: 100},(data) => {
      state.videos=data;
    });
    state.selectVideo=state.videos[state.index];
  }
  
  const videoCompleted = (completed) => {
    
    completed=completed;
  }

  
  const videoSearch = (searchTerm) => {
    let dataset
    
    db.collection('users').doc(props.user.uid).get().then(function(doc){
      if (doc.exists) {
          //searchTerm=searchTerm+doc.data.videoSearchM;
          dataset=doc.data();
          console.log("Document data in video:", doc.data(),dataset.UserVideo,dataset.VideoSearchM);
          state.index=state.index+doc.data.UserVideo;
          getVideo(searchTerm+dataset.VideoSearchM)
      } 
      else{
        console.log("video preference not found")
      }
      
    })
}
  
 
    
    return(
      <div className="videos" >
        
        <SearchBar onSearchTermChange={searchTerm => this.videoSearch(searchTerm)}/>
        <div class="row">
          <div class="container column">
            <VideoDetail 
              video={state.selectedVideo} 
              onCompletion={comp => videoCompleted(comp)} 
              motivation={state.motivates} 
              index={state.index}
            />
        
          </div>
          <div class="container columnList">
            <VideoList
              onVideoSelect={userSelected => this.setState({selectedVideo: userSelected})}
              completed={state.completed}
              index={state.index}
              indexM={state.indexm}
              videos={state.videos}
              onCompletion={completed => videoCompleted(completed)}
              indexChange={indexC => ({index: indexC})}
              indexMChange = {indexMo => this.setState({indexm: indexMo})}
            />
          </div>
        </div>
      </div>
    )
  
}

const mapStateToProps = (state) => ({
  
  mental : state.volunteer.videoSearchM
})
const mapDispatchToProps = (dispatch) => ({
  getVolunteers: () => dispatch({type:GET_VOLUNTEERS})
})

export default connect(mapStateToProps,mapDispatchToProps)(CuratedContents);
