import React,{useState} from 'react';
//import { PageHeader ,Input,Button} from 'antd';
import {auth} from '../../firebase';
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import "../videos/complete.css"
import firebase from 'firebase';

const SignUp = (props) => {
    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')
    const onEmailChange = (event) => setEmail(event.target.value)
    const onPasswordChange=(event)=>setPassword(event.target.value)
    const onSignUp=()=>{
        if(email == '')
            alert("Please enter Email");
        else if(!email.includes('@'))
            alert('Invalid Email');
        else if (password == '')
            alert("Password is empty");
        else if (!(password.match(/[a-z]/g) && password.match( 
            /[A-Z]/g) && password.match( 
            /[0-9]/g) && password.match( 
            /[^a-zA-Z\d]/g) && password.length >= 8)){
                alert("Password should have at least 8 characters and should contain at least 1 uppercase character, 1 lowercase character 1 digit");
        }
        else{
            auth.createUserWithEmailAndPassword(email, password).catch(function(error) {
                // Handle Errors here.
                var errorCode = error.code;
                var errorMessage = error.message;
                // ...
            });
            setEmail('')
            setPassword('')
        }

    }
    const uiConfig = {
        // Popup signin flow rather than redirect flow.
       signInFlow: 'popup',
       // Redirect to /signedIn after sign in is successful. Alternatively you can provide a callbacks.signInSuccess function.
       signInSuccessUrl: '/Home',
       // We will display Google and Facebook as auth providers.
       signInOptions: [
       firebase.auth.GoogleAuthProvider.PROVIDER_ID,
   //firebase.auth.FacebookAuthProvider.PROVIDER_ID
       ]
   }
    return(
        <div className="row">
        <div className="col-md-6 mx-auto">
            <div className="card">
                <div className="card-body">
                    <h1 className="text-center pb-4 pt-3">
                        <span className="text-primary">
                            <i className="fas fa-lock"></i> Sign Up Page
                        </span>
                    </h1>

                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input type="text"
                            className="form-control" 
                            name="email" 
                            required
                            onChange={onEmailChange}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">password</label>
                            <input type="password"
                            className="form-control" 
                            name="password" 
                            required
                            onChange={onPasswordChange}
                            />
                        </div>
                        <StyledFirebaseAuth uiConfig={uiConfig} firebaseAuth={firebase.auth()}/>
                        <div>
                            <a href="sign_in">Already have an account? Sign In</a>
                        </div>

                        <div className="post_input_button">
                        <button onClick={onSignUp} className="btn btn-primary btn-block">Submit</button>
                        </div>

                </div>
            </div>
        </div>

    </div>
    )
}
export default SignUp;
